package week1.day1;

public class Evennumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=0;i<=50;i++)
		{
			if(i%2==0)
			{
				System.out.println("Even number is:"+i);
			}
			/*
			 * else { System.out.println("Odd number is:"+i); }
			 */
		}

	}

}
